settings.css:
.site-nav__link, .site-nav__dropdown-link:not(.site-nav__dropdown-link--top-level),.mobile-nav__link{
font-family: 'bebas_neueregular';
    font-weight: normal;
    text-transform: uppercase;
    letter-spacing: 3px;
}
.site-nav__link .icon-chevron-down{
    vertical-align: baseline;
}
.js-drawer-open-cart{
display:none;
}

.footer__title{
    font-family: 'Montserrat', sans-serif;
    font-weight: 800;
    letter-spacing: 0px;
    font-size: 22px;
}
.site-footer__linklist a{
    font-family: 'Montserrat', sans-serif;
    font-weight: 400;
    font-size: 16px;
    text-transform: uppercase;
    letter-spacing: 2px;
}
.footer__collapsible{
    font-family: 'Montserrat', sans-serif;
    font-weight: 400;
    text-transform: uppercase;
}
@media (max-width:768px){
.footer__title{
  font-size: 18px;
}
}
